        external onlyOperator 
    {
        uint256 counter = 0;
        for(uint256 i=0; i<accounts.length; i++){
            if(accounts[i] != address(0x0)){
                _whitelist.push(accounts[i]); counter++;
            }
        }
        emit AddedWhitelist(accounts.length);
    }

