        address[] memory availables;
        uint256 cn = 0;
        for(uint256 i=0; i<_whitelist.length; i++){
            if(_whitelist[i] != address(0x0) 
                && !mapEnabledWhitelist[_whitelist[i]]) {
                    availables[cn++] = _whitelist[i];
            }
        }
        return availables;
    }

